  useEffect(() => {
    // to send the request
    const getCharacters = () => {
      axios
      .get('https://rickandmortyapi.com/api/character/')
      // if request was successful 
      .then(response =>{
        console.log('The response data: ', response.data.results);
        //to assign the received data to characters
        setCharacters(response.data.results);
      })
